mapper.insert:
INSERT INTO book (
	id, name, author, price
) VALUES (
	#{id}, #{name}, #{author}, #{price}
)

INSERT INTO book (
	id, name, author, price
) VALUES 
	  
		(
			#{item.id}, #{item.name}, #{item.author}, #{item.price}
		)

mapper.select:
SELECT * 
FROM book 
WHERE 1 = 1 
	
		and id = #{id}
	
	
		and name = #{name}
	
	
		and author = #{author}
	
	
		and price = #{price}
